var path    = require("path"),
    log     = require('npmlog'),
    nopt    = require('nopt'),
    pullLib = require('./../lib/pull'),
    commonTools = require('./../lib/common-tools');

var version = commonTools.version,
    cliControl = commonTools.cliControl,
    help = commonTools.help,
    setupDevice = commonTools.setupDevice,
    appdata = commonTools.appdata,
    eula = commonTools.eula;

var processName = path.basename(process.argv[1]).replace(/.js/, '');

process.on('uncaughtException', function(err) {
    log.error('uncaughtException', err.toString());
    log.info('uncaughtException', err.stack);
    cliControl.end(-1);
});

var knownOpts = {
    "device" : [ String, null ],
    "device-list" : Boolean,
    "version" : Boolean,
    "help":  Boolean,
    "ignore":    Boolean,
    "level":    ['silly', 'verbose', 'info', 'http', 'warn', 'error'],
    "wait-for-device":  Boolean
};

var shortHands = {
    "d" : [ "--device" ],
    "D" : [ "--device-list" ],
    "V" : [ "--version" ],
    "h": ["--help"],
    "i": ["--ignore"],
    "v": ["--level", "verbose"],
    "w": ["--wait-for-device"]
};
var argv = nopt(knownOpts, shortHands, process.argv, 2 /** drop 'node' &  'ares-install.js'*/);

log.heading = processName;
log.level = argv.level || 'warn';
log.verbose("argv", argv);

eula.checkEula(function(err) {
    if (err) {
        return finish(err);
    }
    proceed();
});

function proceed () {
    var op;
    if (argv['device-list']) {
        setupDevice.showDeviceListAndExit();
    } else if (argv['version']) {
        version.showVersionAndExit();
    } else if (argv.help) {
        showUsage();
        cliControl.end();
    } else {
        op = pull;
    }

    if (op) {
        version.checkNodeVersion(function(err) {
            op(finish);
        });
    }
}

var options = {
    appId : 'com.ares.defaultName',
    device : argv.device,
    waitForDevice: argv['wait-for-device'],
    ignore: argv['ignore'],
    sourcePath : argv.argv.remain[0],
    destinationPath : argv.argv.remain[1]
};

function showUsage() {
    help.display(processName, appdata.getConfig(true).profile);
}

function pull(next) {
    
    if(!options.destinationPath) {
        options.destinationPath = '.';
    }
    
    if (!options.sourcePath || !options.destinationPath) {
        showUsage();
        cliControl.end(-1);
    }
    pullLib.pull(options,finish);
}

function finish(err, value) {
    log.info("finish():", "err:", err);
    if (err) {
        log.error(processName + ": " + err.toString());
        cliControl.end(-1);
    } else {
        log.info('finish():', value);
        if (value && value.msg) {
            console.log(value.msg);
        }
        cliControl.end();
    }
}
